$plug("trap",function(){var a=this.element;$(window).on("scroll",function(){var b=$($.browser.mozilla?window:document.body).scrollTop();if(!a.next().is(".trap-after"))a.after($("<div/>").addClass("trap-after"));if(!a.data("trap-fp"))a.data("trap-fp",a.position().top);var c=a.next();if(a.data("trap-fp")<=b){a.addClass("trapped");c.css({marginTop:a.outerHeight(true)})}else{a.removeClass("trapped");c.css({marginTop:0})}})});